import "./App.css";
import { P } from "./P";
import { getDate } from "./getDate";
import { useEffect, useState } from "react";
import { LinearProgress } from "@material-ui/core";

function App() {
  const [items, setItems] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);
  const [selectedGame, setSelectedGame] = useState(null);

  function call() {
    setInterval(function () {
      fetch(
        `https://www.balldontlie.io/api/v1/games?per_page=100&dates[]=${getDate()}`
      )
        .then((res) => res.json())
        .then(
          (result) => {
            setIsLoaded(true);
            setItems(result.data);
            // console.log("ran");
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            setIsLoaded(true);
            setError(error);
          }
        );
    }, 45000);
  }

  useEffect(() => {
    fetch(
      `https://www.balldontlie.io/api/v1/games?per_page=100&dates[]=${getDate()}`
    )
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result.data);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
    call();
    console.log(getDate());
  }, []);

  function gameInfo(
    status,
    homeTeam,
    homeTeamScore,
    visitorTeam,
    visitorTeamScore,
    time,
    id
  ) {
    return (
      <div key={id} className="game">
        <p className="scores">
          {homeTeam}:{homeTeamScore} {visitorTeam}:{visitorTeamScore}
        </p>
        <p>
          {status} {time}
        </p>
        <button
          onClick={() => {
            viewGame(id, status);
          }}
        >
          View
        </button>
        <br />
      </div>
    );
  }

  //
  // front page games area
  function listArea() {
    // console.log(items);
    return (
      <div id="list-cont">
        {items.map((item) =>
          gameInfo(
            item.status,
            item.home_team.abbreviation,
            item.home_team_score,
            item.visitor_team.abbreviation,
            item.visitor_team_score,
            item.time,
            item.id
          )
        )}
      </div>
    );
  }

  //
  // view game function area

  function viewGame(gameId, gameStatus) {
    setIsLoaded(false);
    //   initial call
    fetch(`https://www.balldontlie.io/api/v1/stats?game_ids[]=${gameId}`)
      .then((res) => res.json())
      .then(
        (result) => {
          // console.log(result);
          setSelectedGame(result.data);
          setIsLoaded(true);
        },
        (error) => {
          console.log(error);
        }
      );

    //   update every 50sec
    if (gameStatus && selectedGame) {
      setInterval(function () {
        fetch(
          `https://www.balldontlie.io/api/v1/stats?per_page=100&game_ids[]=${gameId}`
        )
          .then((res) => res.json())
          .then(
            (result) => {
              // console.log(result);
              console.log(selectedGame);
              setSelectedGame(result.data);
            },
            (error) => {
              console.log(error);
            }
          );
      }, 50000);
      // console.log(selectedGame);
    }
  }

  function whichTeam(teamId) {
    let team;
    switch (teamId) {
      case 1:
        team = "ATL";
        break;
      case 2:
        team = "BOS";
        break;
      case 3:
        team = "BKN";
        break;
      case 4:
        team = "CHA";
        break;
      case 5:
        team = "CHI";
        break;
      case 6:
        team = "CLE";
        break;
      case 7:
        team = "DAL";
        break;
      case 8:
        team = "DEN";
        break;
      case 9:
        team = "DET";
        break;
      case 10:
        team = "GSW";
        break;
      case 11:
        team = "HOU";
        break;
      case 12:
        team = "IND";
        break;
      case 13:
        team = "LAC";
        break;
      case 14:
        team = "LAL";
        break;
      case 15:
        team = "MEM";
        break;
      case 16:
        team = "MIA";
        break;
      case 17:
        team = "MIL";
        break;
      case 18:
        team = "MIN";
        break;
      case 19:
        team = "NOP";
        break;
      case 20:
        team = "NYK";
        break;
      case 21:
        team = "OKC";
        break;
      case 22:
        team = "ORL";
        break;
      case 23:
        team = "PHI";
        break;
      case 24:
        team = "PHX";
        break;
      case 25:
        team = "POR";
        break;
      case 26:
        team = "SAC";
        break;
      case 27:
        team = "SAS";
        break;
      case 28:
        team = "TOR";
        break;
      case 29:
        team = "UTA";
        break;
      case 30:
        team = "WAS";
        break;
      default:
        team = "WTF";
    }

    return team;
  }

  function showStats(gameObject) {
    console.log(gameObject);
    return (
      <div>
        <h1>
          {whichTeam(gameObject[0].game.home_team_id)}:{" "}
          {gameObject[0].game.home_team_score}
        </h1>
        <h1 className="inline">
          {whichTeam(gameObject[0].game.visitor_team_id)}:{" "}
          {gameObject[0].game.visitor_team_score}
        </h1>
        {gameObject.map((item) => (
          <p>
            {item.player.first_name} {item.player.last_name}
          </p>
        ))}
      </div>
    );
  }

  //
  // start of app return statement
  if (error) {
    {
      console.log(error);

      return <div>Error: {error.message}</div>;
    }
  } else if (!isLoaded) {
    return <LinearProgress id="progressBar" />;
  } else if (selectedGame) {
    //shows individual game
    return (
      <div>
        <button
          onClick={() => {
            setSelectedGame(null);
          }}
        >
          Return
        </button>
        {showStats(selectedGame)}
      </div>
    );
  } else {
    return (
      <div>
        {/* <P text="hi" /> */}
        <div id="title-box-area">
          <span>
            <img
              id="bball"
              src="pics/basketball.gif"
              alt="basketball spinning"
            />
          </span>
          <h1 id="title">NBA Today</h1>
        </div>
        {listArea()}
      </div>
    );
  }
}

export default App;
