import "./App.css";
import { P } from "./P";
import { getDate } from "./getDate";
import { useEffect, useState } from "react";
import { LinearProgress } from "@material-ui/core";

function App() {
  const [items, setItems] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);
  const [selectedGame, setSelectedGame] = useState(null);

  function call() {
    setInterval(function () {
      fetch(
        `https://www.balldontlie.io/api/v1/games?per_page=100&dates[]=${getDate()}`
      )
        .then((res) => res.json())
        .then(
          (result) => {
            setIsLoaded(true);
            setItems(result.data);
            console.log("ran");
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            setIsLoaded(true);
            setError(error);
          }
        );
    }, 45000);
  }

  useEffect(() => {
    fetch(
      `https://www.balldontlie.io/api/v1/games?per_page=100&dates[]=${getDate()}`
    )
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result.data);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
    call();
    console.log(getDate());
  }, []);

  function gameInfo(
    status,
    homeTeam,
    homeTeamScore,
    visitorTeam,
    visitorTeamScore,
    time,
    id
  ) {
    return (
      <div key={id} className="game">
        <p className="scores">
          {homeTeam}:{homeTeamScore} {visitorTeam}:{visitorTeamScore}
        </p>
        <p>
          {status} {time}
        </p>
        <button
          onClick={() => {
            viewGame(id);
          }}
        >
          View
        </button>
        <br />
      </div>
    );
  }

  function listArea() {
    // console.log(items);
    return (
      <div id="list-cont">
        {items.map((item) =>
          gameInfo(
            item.status,
            item.home_team.abbreviation,
            item.home_team_score,
            item.visitor_team.abbreviation,
            item.visitor_team_score,
            item.time,
            item.id
          )
        )}
      </div>
    );
  }

  // view game function area

  function viewGame(gameId) {
    console.log(gameId);
    //   initial call
    fetch(`https://www.balldontlie.io/api/v1/stats?game_ids[]=${gameId}`)
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          setSelectedGame(result);
        },
        (error) => {
          console.log(error);
        }
      );

    //   update every 50sec
    setInterval(function () {
      fetch(
        `https://www.balldontlie.io/api/v1/stats?per_page=100&game_ids[]=${gameId}`
      )
        .then((res) => res.json())
        .then(
          (result) => {
            console.log(result);
            console.log(selectedGame);
          },
          (error) => {
            console.log(error);
          }
        );
    }, 50000);
    console.log(selectedGame);
  }

  //
  // start of app return statement
  if (error) {
    {
      {
        console.log(error);
      }
      return <div>Error: {error.message}</div>;
    }
  } else if (!isLoaded) {
    return <LinearProgress id="progressBar" />;
  } else if (selectedGame) {
    return console.log("game loaded");
  } else {
    return (
      <div>
        {/* <P text="hi" /> */}
        <div id="title-box-area">
          <span>
            <img
              id="bball"
              src="pics/basketball.gif"
              alt="basketball spinning"
            />
          </span>
          <h1 id="title">NBA Today</h1>
        </div>
        {listArea()}
      </div>
    );
  }
}

export default App;
