import { useState } from "react";

export function viewGame(gameId) {
  const [selectedGame, setSelectedGame] = useState(null);
  console.log(gameId);

  //   initial call
  fetch(`https://www.balldontlie.io/api/v1/stats?game_ids[]=${gameId}`)
    .then((res) => res.json())
    .then(
      (result) => {
        console.log(result);
      },
      (error) => {
        console.log(error);
      }
    );

  //   update every 45sec
  setInterval(function () {
    fetch(
      `https://www.balldontlie.io/api/v1/stats?per_page=100&game_ids[]=${gameId}`
    )
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
        },
        (error) => {
          console.log(error);
        }
      );
  }, 50000);
}
