export function viewGame(gameId) {
  console.log(gameId);

  //   initial call
  fetch(`https://www.balldontlie.io/api/v1/stats?game_ids[]=${gameId}`)
    .then((res) => res.json())
    .then(
      (result) => {
        console.log(result);
      },
      (error) => {
        console.log(error);
      }
    );

  //   update every 45sec
  setInterval(function () {
    fetch(`https://www.balldontlie.io/api/v1/stats?game_ids[]=${gameId}`)
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
        },
        (error) => {
          console.log(error);
        }
      );
  }, 5000);
}
