import "./App.css";
import { P } from "./P";
import { getDate } from "./getDate";
import { useEffect, useState } from "react";

function App() {
  const [items, setItems] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);

  function call() {
    setInterval(function () {
      fetch(
        "https://www.balldontlie.io/api/v1/games?per_page=100&dates[]=2020-12-29"
      )
        .then((res) => res.json())
        .then(
          (result) => {
            setIsLoaded(true);
            setItems(result.data);
            console.log("ran");
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            setIsLoaded(true);
            setError(error);
          }
        );
    }, 20000);
  }

  useEffect(() => {
    fetch(
      "https://www.balldontlie.io/api/v1/games?per_page=100&dates[]=2020-12-29"
    )
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result.data);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
    call();
    console.log(getDate());
  }, []);

  function gameInfo(
    status,
    homeTeam,
    homeTeamScore,
    visitorTeam,
    visitorTeamScore,
    time,
    id
  ) {
    return (
      <div key={id}>
        <p>
          {homeTeam}:{homeTeamScore} {visitorTeam}:{visitorTeamScore}
        </p>
        <p>
          {status} {time}
        </p>
        <br />
      </div>
    );
  }

  function listArea() {
    // console.log(items);
    return (
      <div>
        {items.map((item) =>
          gameInfo(
            item.status,
            item.home_team.abbreviation,
            item.home_team_score,
            item.visitor_team.abbreviation,
            item.visitor_team_score,
            item.time,
            item.id
          )
        )}
      </div>
    );
  }

  //
  // start of app return statement
  if (error) {
    {
      {
        console.log(error);
      }
      return <div>Error: {error.message}</div>;
    }
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <div>
        {/* <P text="hi" /> */}
        {listArea()}
      </div>
    );
  }
}

export default App;
