import "./App.css";
import { Img } from "./Img";
import { getDate } from "./getDate";
import { useEffect, useState } from "react";
import { LinearProgress, Button } from "@material-ui/core";

function App() {
  const [items, setItems] = useState([]);
  const [gameStats, setGameStats] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);
  const [selectedGame, setSelectedGame] = useState(null);

  function call() {
    setInterval(function () {
      fetch(
        `https://www.balldontlie.io/api/v1/games?per_page=100&dates[]=${getDate()}`
      )
        .then((res) => res.json())
        .then(
          (result) => {
            setIsLoaded(true);
            setItems(result.data);
            // console.log("ran");
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            setIsLoaded(true);
            setError(error);
          }
        );
    }, 45000);
  }

  useEffect(() => {
    fetch(
      `https://www.balldontlie.io/api/v1/games?per_page=100&dates[]=${getDate()}`
    )
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result.data);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
    call();
    console.log(getDate());
  }, []);

  function gameInfo(
    status,
    homeTeam,
    homeTeamScore,
    visitorTeam,
    visitorTeamScore,
    time,
    id
  ) {
    return (
      <div key={id} className="game">
        <p className="scores">
          {homeTeam}:{homeTeamScore} {visitorTeam}:{visitorTeamScore}
        </p>
        <p>
          {status} {time}
        </p>
        <Button
          className="view-game-btn"
          variant="contained"
          onClick={() => {
            viewGame(id, status);
          }}
        >
          View
        </Button>
        <br />
      </div>
    );
  }

  //
  // front page games area
  function listArea() {
    // console.log(items);
    return (
      <div id="list-cont">
        {items.map((item) =>
          gameInfo(
            item.status,
            item.home_team.abbreviation,
            item.home_team_score,
            item.visitor_team.abbreviation,
            item.visitor_team_score,
            item.time,
            item.id
          )
        )}
      </div>
    );
  }

  //
  // view game function area

  function viewGame(gameId, gameStatus) {
    setIsLoaded(false);
    //   initial call
    fetch(`https://www.balldontlie.io/api/v1/stats?game_ids[]=${gameId}`)
      .then((res) => res.json())
      .then(
        (result) => {
          // console.log(result);
          setSelectedGame(result.data);
          setIsLoaded(true);
          setGameStats(result.data);
          console.log(gameStats);
        },
        (error) => {
          console.log(error);
        }
      );

    //   update every 50sec
    if (gameStatus && selectedGame) {
      setInterval(function () {
        fetch(
          `https://www.balldontlie.io/api/v1/stats?per_page=100&game_ids[]=${gameId}`
        )
          .then((res) => res.json())
          .then(
            (result) => {
              // console.log(result);
              console.log(selectedGame);
              setSelectedGame(result.data);
              setGameStats(result.data);
              console.log(gameStats);
            },
            (error) => {
              console.log(error);
            }
          );
      }, 50000);
      // console.log(selectedGame);
    }
  }

  function whichTeam(teamId) {
    let team;

    switch (teamId) {
      case 1:
        team = "ATL";
        break;
      case 2:
        team = "BOS";
        break;
      case 3:
        team = "BKN";
        break;
      case 4:
        team = "CHA";
        break;
      case 5:
        team = "CHI";
        break;
      case 6:
        team = "CLE";
        break;
      case 7:
        team = "DAL";
        break;
      case 8:
        team = "DEN";
        break;
      case 9:
        team = "DET";
        break;
      case 10:
        team = "GSW";
        break;
      case 11:
        team = "HOU";
        break;
      case 12:
        team = "IND";
        break;
      case 13:
        team = "LAC";
        break;
      case 14:
        team = "LAL";
        break;
      case 15:
        team = "MEM";
        break;
      case 16:
        team = "MIA";
        break;
      case 17:
        team = "MIL";
        break;
      case 18:
        team = "MIN";
        break;
      case 19:
        team = "NOP";
        break;
